import { useState, useEffect } from "react";
import { GifGridItem } from './GifGridItem';

export const GifGrid = ({ category }) => {

  const [images, setImages] = useState([])

  useEffect(() => {
    getGifs();
  }, [])

  const getGifs = async() => {

    const resp = await fetch(`http://api.giphy.com/v1/gifs/search?api_key=9arW6eCRyGoNPsWcpr6qEQhySBlUdcqu&q=${ encodeURI( category ) }&limit=10`);
    const { data } = await resp.json();

    const gifs = data.map( img => {
      return {
        id: img.id,
        title: img.title,
        url: img.images?.downsized_medium.url
      }
    } )

    setImages(gifs);

  }

  return (
    <>
      <h3>{ category }</h3>
      <div className="card-grid">
        <ol>
          {images.map( img => (
            <GifGridItem 
            key={ img.id }
            {...img}
            />
            ))}
        </ol>
      </div>
    </>
  )
}
